import sys

def parse_parenthesis_getpos(input_string):
    retval = 0
    for i, char in enumerate(input_string):
        if char == '(':
            retval += 1
        elif char == ')':
            retval -= 1
        if retval == -1:
            return i + 1
    return i + 1

